import { ethers } from 'ethers';
import { useEffect, useState } from 'react';

import close from '../assets/close.svg';

const Home = ({ home, provider, account, escrow, togglePop }) => {

    const [hasBought, setHasBought] = useState(false)
    const [hasLended, setHasLended] = useState(false)
    const [hasInspected, setHasInspected] = useState(false)
    const [hasSold, setHasSold] = useState(false)

    const [buyer, setBuyer] = useState(null)
    const [lender, setLender] = useState(null)
    const [inspector, setInspector] = useState(null)
    const [seller, setSeller] = useState(null)

    const [owner, setOwner] = useState(null)

    const fetchDetails = async () => {

        // Buyer
        const buyer = await escrow.buyer(home.id)
        setBuyer(buyer)

        const hasBought = await escrow.approval(home.id, buyer)
        setHasBought(hasBought)

        // Seller
        const seller = await escrow.seller()
        setSeller(seller)

        const hasSold = await escrow.approval(home.id, seller)
        setHasSold(hasSold)

        // Lender
        const lender = await escrow.lender()
        setLender(lender)

        const hasLended = await escrow.approval(home.id, lender)
        setHasLended(hasLended)

        // Inspector
        const inspector = await escrow.inspector()
        setInspector(inspector)

        const hasInspected = await escrow.inspectionPassed(home.id)
        setHasInspected(hasInspected)

    }

    // I guess once the escrow contract deal is approved by all parties THEN only the below function is accessible?...
    const fetchOwner = async () => {
        if (await escrow.isListed(home.id)) return

        const owner = await escrow.buyer(home.id)
        setOwner(owner)
    }

    // Now we want to do useEffect on the above 2 functions
    // Whenever the [hasSold] value changes, it will trigger a recall/refetch of the functions inside of useEffect function.
    useEffect(() => {
        fetchDetails()
        fetchOwner()
    }, [hasSold]) // whenever this value changes, it triggers a refetch/recall of the stuff inside the useEffect function.

    return (
        <div className="home">
            <div className="home__details">
                <div className="home_image">
                    <img src={home.image} alt="Home"/>
                </div>

                <div className="home__overview">
                    <h1>{home.name}</h1>
                    <p>
                        <strong>{home.attributes[2].value}</strong> bds |
                        <strong>{home.attributes[3].value}</strong> ba |
                        <strong>{home.attributes[4].value}</strong> sqft   
                    </p>
                    <p>{home.address}</p>
                    <h4>{home.attributes[0].value} ETH</h4>

                    {/* conditional: if (new) owner exists, then . */}
                    {owner ? (
                        <div className='home__owned'>
                            Owned by {owner.slice(0,6) + '...' + owner.slice(38,42)}
                        </div>
                    ) : (
                        <div>
                            {(account === inspector) ? (
                                <button className="home__buy" >
                                    Approve Inspection
                                </button>
                            ) : (account === lender) ? (
                                <button className="home__buy" >
                                    Approve & Lend
                                </button>
                            ) : (account === seller) ? (
                                <button className="home__buy" >
                                    Approve & Sell
                                </button>
                            ) : (
                                <button className="home__buy" >
                                    Buy
                                </button>
                            )}

                            <button className='home__contact'>
                                Contact agent
                            </button>

                        </div>                        
                    )}

                    <hr />

                    <h2>Overview</h2>

                    <p>
                        {home.description}
                    </p>

                    <hr />

                    <h2>Facts and features</h2>

                    <ul>
                        {home.attributes.map((attribute, index) => (
                            <li key={index}><strong>{attribute.trait_type}</strong> : {attribute.value}</li>
                        ))}                    
                    </ul>

                </div>

                <button onClick={togglePop} className="home__close">
                    <img src={close} alt="Close"/>
                </button>

            </div>
        </div>
    );
}

export default Home;
